  		($perlstring =~ /^"/) ? $perlstring : qq["$perlstring"];
  	};
  }
  
  unless (exists &B::cstring)
  {
  	*B::cstring = \&B::perlstring;
  }
  
  push @B::EXPORT_OK, qw( perlstring cstring );
  
  #### Done!
  
  5.6;
  
  __END__
  
