  ###
  # Neighbourhood.
  #
  
  sub vertices_at {
      my $g = shift;
      my $V = $g->[ _V ];
      return @_ unless ($V->[ _f ] & _HYPER);
      my %v;
      my @i;
      for my $v ( @_ ) {
  	my $i = $V->_get_path_id( $v );
  	return unless defined $i;
  	push @i, ( $v{ $v } = $i );
      }
      my $Vi = $V->_ids;
      my @v;
      while (my ($i, $v) = each %{ $Vi }) {
  	my %i;
  	my $h = $V->[_f ] & _HYPER;
  	@i{ @i } = @i if @i; # @todo: nonuniq hyper vertices?
  	for my $u (ref $v eq 'ARRAY' && $h ? @$v : $v) {
  	    my $j = exists $v{ $u } ? $v{ $u } : ( $v{ $u } = $i );
  	    if (defined $j && exists $i{ $j }) {
  		delete $i{ $j };
  		unless (keys %i) {
  		    push @v, $v;
  		    last;
  		}
  	    }
  	}
      }
      return @v;
  }
  
  sub _edges_at {
      my $g = shift;
      my $V = $g->[ _V ];
      my $E = $g->[ _E ];
      my @e;
      my $en = 0;
      my %ev;
      my $h = $V->[_f ] & _HYPER;
      for my $v ( $h ? $g->vertices_at( @_ ) : @_ ) {
  	my $vi = $V->_get_path_id( ref $v eq 'ARRAY' && $h ? @$v : $v );
  	next unless defined $vi;
  	my $Ei = $E->_ids;
  	while (my ($ei, $ev) = each %{ $Ei }) {
  	    if (wantarray) {
  		for my $j (@$ev) {
  		    push @e, [ $ei, $ev ]
  			if $j == $vi && !$ev{$ei}++;
  		}
  	    } else {
  		for my $j (@$ev) {
  		    $en++ if $j == $vi;
  		}
  	    }		    
  	}
      }
      return wantarray ? @e : $en;
  }
  
  sub _edges {
      my $g = shift;
      my $n = pop;
      my $i = $n == _S ? 0 : -1;  # _edges_from() or _edges_to()
      my $V = $g->[ _V ];
      my $E = $g->[ _E ];
      my $N = $g->[ $n ];
      my $h = $V->[ _f ] & _HYPER;
      unless (defined $N && $N->[ 0 ] == $g->[ _G ]) {
  	$g->[ $n ]->[ 1 ] = { };
  	$N = $g->[ $n ];
  	my $u = $E->[ _f ] & _UNORD;
  	my $Ei = $E->_ids;
  	while (my ($ei, $ev) = each %{ $Ei }) {
  	    next unless @$ev;
  	    my $e = [ $ei, $ev ];
  	    if ($u) {
  		push @{ $N->[ 1 ]->{ $ev->[ 0] } }, $e;
  		push @{ $N->[ 1 ]->{ $ev->[-1] } }, $e;
  	    } else {
  		my $e = [ $ei, $ev ];
  		push @{ $N->[ 1 ]->{ $ev->[$i] } }, $e;
  	    }
  	}
  	$N->[ 0 ] = $g->[ _G ];
      }
      my @e;
      my @at = $h ? $g->vertices_at( @_ ) : @_;
      my %at; @at{@at} = ();
      for my $v ( @at ) {
  	my $vi = $V->_get_path_id( ref $v eq 'ARRAY' && $h ? @$v : $v );
  	next unless defined $vi && exists $N->[ 1 ]->{ $vi };
  	push @e, @{ $N->[ 1 ]->{ $vi } };
      }
      if (wantarray && $g->is_undirected) {
  	my @i = map { $V->_get_path_id( $_ ) } @_;
  	for my $e ( @e ) {
  	    unless ( $e->[ 1 ]->[ $i ] == $i[ $i ] ) {
  		$e = [ $e->[ 0 ], [ reverse @{ $e->[ 1 ] } ] ];
  	    }
  	}
      }
      return @e;
  }
  
  sub _edges_from {
      push @_, _S;
