  sub _all_successors {
      my $g = shift;
      my @init = @_;
      my %todo;
      @todo{@init} = @init;
      my %seen;
      my %init = %todo;
      my %self;
      while (keys %todo) {
        my @todo = values %todo;
        for my $t (@todo) {
  	$seen{$t} = delete $todo{$t};
  	for my $s ($g->successors($t)) {
  	  $self{$s} = $s if exists $init{$s};
  	  $todo{$s} = $s unless exists $seen{$s};
  	}
        }
      }
      for my $v (@init) {
        delete $seen{$v} unless $g->has_edge($v, $v) || $self{$v};
      }
      return values %seen;
  }
  
  sub all_successors {
