  sub _all_predecessors {
      my $g = shift;
      my @init = @_;
      my %todo;
      @todo{@init} = @init;
      my %seen;
      my %init = %todo;
      my %self;
      while (keys %todo) {
        my @todo = values %todo;
        for my $t (@todo) {
  	$seen{$t} = delete $todo{$t};
  	for my $p ($g->predecessors($t)) {
  	  $self{$p} = $p if exists $init{$p};
  	  $todo{$p} = $p unless exists $seen{$p};
  	}
        }
      }
      for my $v (@init) {
        delete $seen{$v} unless $g->has_edge($v, $v) || $self{$v};
      }
      return values %seen;
  }
  
