  sub delete_edge {
      my $g = shift;
      $g->expect_non_unionfind;
      my @i = $g->_vertex_ids( @_ );
      return $g unless @i;
      my $i = $g->[ _E ]->_get_path_id( @i );
      return $g unless defined $i;
      $g->[ _E ]->_del_id( $i );
      $g->[ _G ]++;
