  sub _set_edge_attribute {
      my $g = shift;
      my $value = pop;
      my $attr  = pop;
      my $E = $g->[ _E ];
      my $f = $E->[ _f ];
      my @i;
      if ($E->[ _a ] == 2 && @_ == 2 && !($f & (_HYPER|_REF|_UNIQ))) { # Fast path.
  	@_ = sort @_ if ($f & _UNORD);
  	my $s = $E->[ _s ];
