  sub vertex {
      my $g = shift;
      $g->has_vertex( @_ ) ? @_ : undef;
  }
  
  sub out_edges {
      my $g = shift;
      return unless @_ && $g->has_vertex( @_ );
      my @e = $g->edges_from( @_ );
      wantarray ? map { @$_ } @e : @e;
  }
  
  sub in_edges {
      my $g = shift;
      return unless @_ && $g->has_vertex( @_ );
      my @e = $g->edges_to( @_ );
      wantarray ? map { @$_ } @e : @e;
  }
  
  sub add_vertices {
      my $g = shift;
      $g->add_vertex( $_ ) for @_;
      return $g;
  }
  
  sub add_edges {
      my $g = shift;
      while (@_) {
  	my $u = shift @_;
  	if (ref $u eq 'ARRAY') {
  	    $g->add_edge( @$u );
  	} else {
  	    if (@_) {
  		my $v = shift @_;
  		$g->add_edge( $u, $v );
  	    } else {
  		require Carp;
  		Carp::croak("Graph::add_edges: missing end vertex");
  	    }
  	}
      }
      return $g;
  }
  
