  sub copy {
      my $g = shift;
      my %opt = _get_options( \@_ );
  
      my $c =
  	(ref $g)->new(map { $_ => $g->$_ ? 1 : 0 }
  		      qw(directed
  			 compat02
  			 refvertexed
  			 hypervertexed
  			 countvertexed
  			 multivertexed
  			 hyperedged
  			 countedged
  			 multiedged
  			 omniedged
  		         __stringified));
      for my $v ($g->isolated_vertices) { $c->add_vertex($v) }
      for my $e ($g->edges05)           { $c->add_edge(@$e)  }
  
      return $c;
  }
  
  *copy_graph = \&copy;
  
  sub _deep_copy_Storable {
      my $g = shift;
      my $safe = new Safe;
      local $Storable::Deparse = 1;
      local $Storable::Eval = sub { $safe->reval($_[0]) };
      return Storable::thaw(Storable::freeze($g));
  }
  
  sub _deep_copy_DataDumper {
      my $g = shift;
      my $d = Data::Dumper->new([$g]);
      use vars qw($VAR1);
      $d->Purity(1)->Terse(1)->Deepcopy(1);
      $d->Deparse(1) if $] >= 5.008;
      eval $d->Dump;
  }
  
  sub deep_copy {
      if (_can_deep_copy_Storable()) {
  	return _deep_copy_Storable(@_);
      } else {
  	return _deep_copy_DataDumper(@_);
      }
  }
  
  *deep_copy_graph = \&deep_copy;
  
  sub transpose_edge {
      my $g = shift;
      if ($g->is_directed) {
  	return undef unless $g->has_edge( @_ );
  	my $c = $g->get_edge_count( @_ );
  	my $a = $g->get_edge_attributes( @_ );
  	my @e = reverse @_;
  	$g->delete_edge( @_ ) unless $g->has_edge( @e );
  	$g->add_edge( @e ) for 1..$c;
  	$g->set_edge_attributes(@e, $a) if $a;
      }
      return $g;
  }
  
  sub transpose_graph {
      my $g = shift;
      my $t = $g->copy;
      if ($t->directed) {
  	for my $e ($t->edges05) {
  	    $t->transpose_edge(@$e);
  	}
      }
      return $t;
  }
  
  *transpose = \&transpose_graph;
  
  sub complete_graph {
      my $g = shift;
      my $c = $g->new( directed => $g->directed );
      my @v = $g->vertices05;
      for (my $i = 0; $i <= $#v; $i++ ) {
  	for (my $j = 0; $j <= $#v; $j++ ) {
  	    next if $i >= $j;
  	    if ($g->is_undirected) {
  		$c->add_edge($v[$i], $v[$j]);
  	    } else {
  		$c->add_edge($v[$i], $v[$j]);
  		$c->add_edge($v[$j], $v[$i]);
  	    }
  	}
      }
      return $c;
  }
  
  *complement = \&complement_graph;
  
  sub complement_graph {
      my $g = shift;
      my $c = $g->new( directed => $g->directed );
      my @v = $g->vertices05;
      for (my $i = 0; $i <= $#v; $i++ ) {
  	for (my $j = 0; $j <= $#v; $j++ ) {
  	    next if $i >= $j;
  	    if ($g->is_undirected) {
  		$c->add_edge($v[$i], $v[$j])
  		    unless $g->has_edge($v[$i], $v[$j]);
  	    } else {
  		$c->add_edge($v[$i], $v[$j])
  		    unless $g->has_edge($v[$i], $v[$j]);
  		$c->add_edge($v[$j], $v[$i])
  		    unless $g->has_edge($v[$j], $v[$i]);
  	    }
  	}
      }
      return $c;
  }
  
  *complete = \&complete_graph;
  
  sub subgraph {
    my ($g, $src, $dst) = @_;
    $dst = $src unless defined $dst;
    unless (ref $src eq 'ARRAY' && ref $dst eq 'ARRAY') {
      Carp::croak("Graph::subgraph: need src and dst array references");
    }
    my $s = $g->new;
    my @u = grep { $g->has_vertex($_) } @$src;
    my @v = grep { $g->has_vertex($_) } @$dst;
    $s->add_vertices(@u, @v);
    for my $u (@u) {
      my @e;
      for my $v (@v) {
        if ($g->has_edge($u, $v)) {
          push @e, [$u, $v];
        }
      }
      $s->add_edges(@e);
    }
    return $s;
  }
  
