  sub density_limits {
      my $g = shift;
  
      my $V = $g->vertices05;
      my $M = $V * ($V - 1);
  
      $M /= 2 if $g->is_undirected;
  
      return ( 0.25 * $M, 0.75 * $M, $M );
  }
  
  sub density {
      my $g = shift;
      my ($sparse, $dense, $complete) = $g->density_limits;
  
      return $complete ? $g->edges / $complete : 0;
  }
  
