  sub _attr02_012 {
      my ($g, $op, $ga, $va, $ea) = splice @_, 0, 5;
      if ($g->is_compat02) {
  	if    (@_ == 0) { return $ga->( $g ) }
  	elsif (@_ == 1) { return $va->( $g, @_ ) }
  	elsif (@_ == 2) { return $ea->( $g, @_ ) }
  	else {
  	    die sprintf "$op: wrong number of arguments (%d)", scalar @_;
  	}
      } else {
  	die "$op: not a compat02 graph"
      }
  }
  
