      if ($g->is_compat02) {
  	if    (@_ == 1) { return $ga->( $g, @_ ) }
  	elsif (@_ == 2) { return $va->( $g, @_[1, 0] ) }
  	elsif (@_ == 3) { return $ea->( $g, @_[1, 2, 0] ) }
