  sub topological_sort {
      my $g = shift;
      my %opt = _get_options( \@_ );
      my $eic = $opt{ empty_if_cyclic };
      my $hac;
      if ($eic) {
  	$hac = $g->has_a_cycle;
      } else {
  	$g->expect_dag;
      }
      delete $opt{ empty_if_cyclic };
      my $t = Graph::Traversal::DFS->new($g, %opt);
      my @s = $t->dfs;
      $hac ? () : reverse @s;
  }
  
