  sub _undirected_copy_compute {
    my $g = shift;
    my $c = Graph::Undirected->new;
    for my $v ($g->isolated_vertices) { # TODO: if iv ...
      $c->add_vertex($v);
    }
    for my $e ($g->edges05) {
      $c->add_edge(@$e);
    }
    return $c;
  }
  
