  ###
  # Connected components.
  #
  
  sub _connected_components_compute {
      my $g = shift;
      my %cce;
      my %cci;
      my $cc = 0;
      if ($g->has_union_find) {
  	my $UF = $g->_get_union_find();
  	my $V  = $g->[ _V ];
  	my %icce; # Isolated vertices.
  	my %icci;
  	my $icc = 0;
  	for my $v ( $g->unique_vertices ) {
  	    $cc = $UF->find( $V->_get_path_id( $v ) );
  	    if (defined $cc) {
  		$cce{ $v } = $cc;
  		push @{ $cci{ $cc } }, $v;
  	    } else {
  		$icce{ $v } = $icc;
  		push @{ $icci{ $icc } }, $v;
  		$icc++;
  	    }
  	}
  	if ($icc) {
  	    @cce{ keys %icce } = values %icce;
  	    @cci{ keys %icci } = values %icci;
  	}
      } else {
  	my @u = $g->unique_vertices;
  	my %r; @r{ @u } = @u;
  	my $froot = sub {
  	    (each %r)[1];
  	};
  	my $nroot = sub {
  	    $cc++ if keys %r;
  	    (each %r)[1];
  	};
  	my $t = Graph::Traversal::DFS->new($g,
  					   first_root => $froot,
  					   next_root  => $nroot,
  					   pre => sub {
  					       my ($v, $t) = @_;
  					       $cce{ $v } = $cc;
  					       push @{ $cci{ $cc } }, $v;
  					       delete $r{ $v };
  					   },
  					   @_);
  	$t->dfs;
      }
      return [ \%cce, \%cci ];
  }
  
  sub _connected_components {
      my $g = shift;
      my $ccc = _check_cache($g, 'connectivity',
  			   \&_connected_components_compute, @_);
      return @{ $ccc };
  }
  
