  sub _strongly_connected_components_compute {
      my $g = shift;
      my $t = Graph::Traversal::DFS->new($g);
      my @d = reverse $t->dfs;
      my @c;
      my $h = $g->transpose_graph;
      my $u =
  	Graph::Traversal::DFS->new($h,
  				   next_root => sub {
  				       my ($t, $u) = @_;
  				       my $root;
  				       while (defined($root = shift @d)) {
  					   last if exists $u->{ $root };
  				       }
  				       if (defined $root) {
  					   push @c, [];
  					   return $root;
  				       } else {
  					   return;
  				       }
  				   },
  				   pre => sub {
  				       my ($v, $t) = @_;
  				       push @{ $c[-1] }, $v;
  				   },
  				   @_);
      $u->dfs;
      return \@c;
  }
  
