      $Cb{$_} = 0 for @V;
  
      for my $s (@V) {
  	my @S; # stack (unshift, shift)
  
  	my %P; # P{w} = empty list
  	$P{$_} = [] for @V;
  
  	my %sigma; # \sigma{t} = 0
  	$sigma{$_} = 0 for @V;
  	$sigma{$s} = 1;
  
  	my %d; # d{t} = -1;
  	$d{$_} = -1 for @V;
  	$d{$s} = 0;
  
  	my @Q; # queue (push, shift)
  	push @Q, $s;
  
  	while (@Q) {
  	    my $v = shift @Q;
  	    unshift @S, $v;
  	    for my $w ($g->successors($v)) {
  		# w found for first time
  		if ($d{$w} < 0) {
  		    push @Q, $w;
  		    $d{$w} = $d{$v} + 1;
  		}
  		# Shortest path to w via v
  		if ($d{$w} == $d{$v} + 1) {
  		    $sigma{$w} += $sigma{$v};
  		    push @{ $P{$w} }, $v;
  		}
  	    }
  	}
  
  	my %delta;
  	$delta{$_} = 0 for @V;
  
  	while (@S) {
  	    my $w = shift @S;
  	    for my $v (@{ $P{$w} }) {
  		$delta{$v} += $sigma{$v}/$sigma{$w} * (1 + $delta{$w});
  	    }
  	    if ($w ne $s) {
  		$Cb{$w} += $delta{$w};
  	    }
  	}
      }
  
      return %Cb;
  }
  
