  sub new {
      my $class = shift;
      my $g = shift;
      unless (ref $g && $g->isa('Graph')) {
  	require Carp;
  	Carp::croak("Graph::Traversal: first argument is not a Graph");
      }
      my $self = { graph => $g, state => { } };
      bless $self, $class;
      $self->reset;
      $self->configure( @_ );
      return $self;
