  sub terminate {
      my $self = shift;
      $self->{ terminate } = 1;
  }
  
  sub add_order {
      my ($self, @next) = @_;
      push @{ $self->{ order } }, @next;
  }
  
  sub visit {
      my ($self, @next) = @_;
      delete @{ $self->{ unseen } }{ @next };
      print "unseen = @{[sort keys %{$self->{unseen}}]}\n" if DEBUG;
      @{ $self->{ seen } }{ @next } = @next;
      print "seen = @{[sort keys %{$self->{seen}}]}\n" if DEBUG;
      $self->{ add }->( $self, @next );
      print "order = @{$self->{order}}\n" if DEBUG;
      if (exists $self->{ pre }) {
  	my $p = $self->{ pre };
  	for my $v (@next) {
  	    $p->( $v, $self );
  	}
      }
  }
  
  sub visit_preorder {
      my ($self, @next) = @_;
