  	$self->{ preordern }->{ $v } = $self->{ preorderi }++;
      }
      print "preorder = @{$self->{preorder}}\n" if DEBUG;
      $self->visit( @next );
  }
  
  sub visit_postorder {
      my ($self) = @_;
