  	print "next.0 - @next\n" if DEBUG;
  	my %next; @next{ @next } = @next;
  	print "next.1 - @next\n" if DEBUG;
  	@next = values %next;
  	my @all = @next;
  	print "all = @all\n" if DEBUG;
