  	print "next.5 - @next\n" if DEBUG;
  	push @{ $self->{ roots } }, $next[0];
      }
      print "next.6 - @next\n" if DEBUG;
      if (@next) {
  	$self->visit_preorder( @next );
      }
      return $next[0];
  }
  
  sub _order {
      my ($self, $order) = @_;
