  package Graph::UnionFind;
  
  use strict;
  
  sub _PARENT  () { 0 }
  sub _RANK    () { 1 }
  
  sub new {
      my $class = shift;
      bless { }, $class;
  }
  
  sub add {
      my ($self, $elem) = @_;
      $self->{ $elem } = [ $elem, 0 ] unless defined $self->{$elem};
  }
  
  sub has {
      my ($self, $elem) = @_;
      exists $self->{ $elem };
  }
  
  sub _parent {
      return undef unless defined $_[1];
      if (@_ == 2) {
  	exists $_[0]->{ $_[ 1 ] } ? $_[0]->{ $_[1] }->[ _PARENT ] : undef;
      } elsif (@_ == 3) {
  	$_[0]->{ $_[1] }->[ _PARENT ] = $_[2];
      } else {
  	require Carp;
  	Carp::croak(__PACKAGE__ . "::_parent: bad arity");
      }
  }
  
  sub _rank {
      return unless defined $_[1];
      if (@_ == 2) {
  	exists $_[0]->{ $_[1] } ? $_[0]->{ $_[1] }->[ _RANK ] : undef;
      } elsif (@_ == 3) {
  	$_[0]->{ $_[1] }->[ _RANK ] = $_[2];
      } else {
  	require Carp;
  	Carp::croak(__PACKAGE__ . "::_rank: bad arity");
      }
  }
  
  sub find {
      my ($self, $x) = @_;
      my $px = $self->_parent( $x );
      return unless defined $px;
      $self->_parent( $x, $self->find( $px ) ) if $px ne $x;
      $self->_parent( $x );
  }
  
  sub union {
      my ($self, $x, $y) = @_;
      $self->add($x) unless $self->has($x);
      $self->add($y) unless $self->has($y);
      my $px = $self->find( $x );
      my $py = $self->find( $y );
      return if $px eq $py;
      my $rx = $self->_rank( $px );
      my $ry = $self->_rank( $py );
      # print "union($x, $y): px = $px, py = $py, rx = $rx, ry = $ry\n";
      if ( $rx > $ry ) {
  	$self->_parent( $py, $px );
      } else {
  	$self->_parent( $px, $py );
  	$self->_rank( $py, $ry + 1 ) if $rx == $ry;
      }
  }
  
  sub same {
      my ($uf, $u, $v) = @_;
      my $fu = $uf->find($u);
      return undef unless defined $fu;
      my $fv = $uf->find($v);
      return undef unless defined $fv;
      $fu eq $fv;
  }
  
  1;
