              };
  
              if ( $do_back_escape ) {
                  $body_A =~ s/\\/\\\\/g;
                  $body_B =~ s/\\/\\\\/g;
              }
  
              my $line_A = join $body_A, $l_ws_A, $t_ws_A;
              my $line_B = join $body_B, $l_ws_B, $t_ws_B;
  
              unless ( $do_tab_escape ) {
                  $line_A = expand_tabs $line_A;
                  $line_B = expand_tabs $line_B;
              }
  
              $A->[1] = escape $line_A;
              $B->[1] = escape $line_B;
          }
  
          push @elts, [ @$A, @$B, $elt_type ];
      }
  
      push @{$self->{ELTS}}, @elts, ["bar"];
      return "";
